import React, { Component } from 'react';
import './Card.css';
import { bake_cookie, read_cookie } from 'sfcookies';

// destructuring
class Card extends Component {
    constructor(props) {
        super(props);
        this.state = {
            unlocked: read_cookie('lockStatus')
        }
    }

    removeClass = (id, name, count, countReq) => {
        let parent = document.getElementById(id);
        //preventing the null from calling functions
        if (parent) {
            console.log(count);
            if (count >= countReq && !this.state.unlocked) {
                alert(`Congrats! You've unlocked a ${name}!`);
                parent.classList.remove('parent');
                parent.children[0].classList.add('z-1');
                this.setState({ unlocked: true });
                bake_cookie('lockStatus', this.state.unlocked);
            } else {
                if (this.state.unlocked) {
                    //do nothing
                    console.log(this.state.unlocked);
                } else {
                    alert('Sorry.. your reward is locked..');
                }
            }
        }
    }

    render() {
        const { count, countReq, id, name, height, weight, type } = this.props;
        return (
            <div className="wrapper" style={{ flexBasis: '350px' }}>
                <div id={id} onClick={() => this.removeClass(id, name, count, countReq)} className="br3 pa10 ma3 bw2 parent">
                    <div className="tc bg-washed-blue br3 pa10 ma2 bw3 shadow-3 ba grow child">
                        <h4 className="tl ma1 b bb">&nbsp; {name}</h4>
                        <img className="pa1" alt="pokemon" src={`https://pokeres.bastionbot.org/images/pokemon/${id}.png`} width="200" height="200" />
                        <div className="pa3">
                            <div className="bw2 ba pa1 bg-washed-green">
                                <p className="ma0 pa1 bt">No. <strong>{id}</strong></p>
                                <p className="b pa1">
                                    Height: {height} <br />
                                Weight: {weight} <br />
                                Type: {type}
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Card;
