import React, { Component } from 'react';
import './Reminder.css';
import { relativeTimeThreshold } from 'moment';

//instead of passing props.onInputChange, destructure it instead
class Reminder extends Component {
    constructor() {
        super();
        this.state = {
            input: '',
            reminders: []
        }
    }

    onInputChange = (event) => {
        this.setState({ input: event.target.value });
    }

    renderReminders = () => {
        this.state.reminders.push(this.state.input)
        this.setState({ input: '' })
        document.getElementById("myForm").value = '';
        let ul = document.getElementById('ul');
        let li = document.createElement('li').classList.add('list-group-item');
        ul.appendChild(li);
        li.innerHTML += this.state.input;
    }

    render() {
        return (
            <div className="App">
                <div fontSize="10px" className="title">
                    Reminder Pro
                </div>
                <div className="form-inline">

                    <div className="form-group">
                        <input
                            id="myForm"
                            onChange={this.onInputChange}
                            className="form-control"
                            placeholder="Insert item name"
                        />
                    </div>

                        <button
                        onClick={this.renderReminders}
                        className="btn btn-success grow"
                        type="button">
                        Add Reminder
                        </button>

                        <div>
                            <ul id="ul" className="list-group col-sm-4">

                            </ul>
                        </div>

                </div>
            </div>
        );
    }
}

export default Reminder;
