import React, { Component } from 'react';
import './Reminder.css';
import moment from 'moment';
import Push from 'push.js';
import { bake_cookie, read_cookie } from 'sfcookies';


//instead of passing props.onInputChange, destructure it instead
class Milestones extends Component {
    constructor(props) {
        super(props);
        this.state = {
            input: '',
            dueDate: '',
            reminders: read_cookie('reminders'),
        }
    }

    render() {
        return (
            <div className="App">
                <button
                    onClick={() => onRouteChange('signin')}
                    className="btn btn-secondary signout-button black grow shadow-4"
                    type="button">
                    Sign Out
                        </button>
                <div className="title br3">
                    <div className="header">
                        <iframe
                            className="clock mb3"
                            src="http://free.timeanddate.com/clock/i7b48e4m/n236/szw110/szh110/hocddd/hbw0/hfc000/cf100/hgr0/hwc000/fav0/fiv0/mqcfff/mql15/mqw8/mqd100/mhcfff/mhl15/mhw4/mhd100/mmv0/hhcff9/hmcff9"
                            frameBorder="0"
                            width="150"
                            height="150"
                        >
                        </iframe>
                        <hr></hr>
                        <h1 className="shadow-5 pa2"><strong>B U R P E E </strong></h1>
                    </div>
                </div>
                <div className="form-inline">
                    <div className="reminder-container">
                        <div className="reminder-inputs br3 shadow-5">
                            <input
                                id="myForm"
                                onChange={this.onInputChange}
                                className="form-control item-input"
                                placeholder="Insert item name"
                            />

                            <input
                                id="myForm"
                                onChange={this.onDateChange}
                                className="form-control"
                                type="datetime-local"
                            />

                            <button
                                onClick={this.wrapperAdd}
                                className="add-btn btn btn-success grow add-button br2 shadow-4"
                                type="button">
                                Add Reminder
                            </button>
                        </div>
                        <div>
                            {this.displayReminders()}
                        </div>
                        {/* render reminders */}
                        {this.state.reminders.forEach(reminder => {
                            if (!executed && moment(new Date(reminder.dueDate)).fromNow() === 'in 7 days') {
                                runReminders
                                    .push(this.renderExpiryNotifications(reminder.text,
                                        moment(new Date(reminder.dueDate)).fromNow()));
                            }
                        })}

                        {/* stop rendering reminders */}
                        {executed = true}

                        <button
                            onClick={this.clearReminders}
                            className="btn btn-secondary achieve-button grow shadow-4"
                            type="button">
                            Milestones
                            </button>

                        <button
                            onClick={this.clearReminders}
                            className="btn btn-secondary clear-button grow shadow-4"
                            type="button">
                            Clear All
                            </button>
                    </div>

                </div>
            </div>
        );
    }
}

export default Reminder;
