import React from 'react';

   // destructuring
const Card = ({ name, email, id }) => {
    return (
        <div className="tc bg-light-green dib br3 pa10 ma2 grow bw2 shadow-5">
            <img className="w200 h-5" alt="pokemon" src={`https://pokeres.bastionbot.org/images/pokemon/1.png`} />
            <div>
                <h2>{name}</h2>
                <p>{email}</p>
            </div>
        </div>
        
    );
}

export default Card;
