import React, { Component } from 'react';
import './Milestone.css';
import Card from './Card';
import Push from 'push.js';
import { bake_cookie, read_cookie } from 'sfcookies';


//instead of passing props.onInputChange, destructure it instead
class Milestone extends Component {
    constructor(props) {
        super(props);
    }

    fetchPokemonData = (pokemon) => {
        // <--- this is saving the pokemon url to a      variable to us in a fetch.(Ex: https://pokeapi.co/api/v2/pokemon/1/)
        let url = pokemon.url
        fetch(url)
            .then(response => response.json())
            .then(function (pokeData) {
                console.log(pokeData)
            })
    }

    componentDidMount() {
        fetch('https://pokeapi.co/api/v2/pokemon?limit=151')
            .then(response => response.json())
            .then(function (allpokemon) {
                allpokemon.results.forEach(function (pokemon) {
                    this.fetchPokemonData(pokemon);
                })
            });
    }

    render() {
        const { onRouteChange } = this.props;
        return (
            <div className="Milestone">
                <button
                    onClick={() => onRouteChange('signin')}
                    className="btn btn-secondary signout-button black grow shadow-4"
                    type="button">
                    Sign Out
                    </button>

                <Card />
                {console.log('hi')}
                <button
                    onClick={() => onRouteChange('home')}
                    className="btn btn-secondary home-button black grow shadow-4"
                    type="button">
                    Home
                    </button>
            </div>
        );
    }
}

export default Milestone;
