import React, { Component } from 'react';
import './Reminder.css';
import { relativeTimeThreshold } from 'moment';

//instead of passing props.onInputChange, destructure it instead
class Reminder extends Component {
    constructor() {
        super();
        this.state = {
            input: '',
            reminders: []
        }
    }

    onInputChange = (event) => {
        this.setState({ input: event.target.value });
    }

    renderReminders = () => {
        this.state.reminders.push(this.state.input)
        this.setState({ input: '' })
        document.getElementById("myForm").value = '';
        console.log(this.state.reminders, this.state.input)

        listContainer = document.createElement('div');
        listElement = document.createElement('ul');

        var listItem;
        this.state.reminders.forEach(reminder => {
            return (
                listItem = document.createElement('li'),
                listItem.innerHTML = reminder,
                listElement.appendChild(listItem)
            )
        })

    }

    render() {
        return (
            <div className="App">
                <div fontSize="10px" className="title">
                    Reminder Pro
                </div>
                <div className="form-inline">

                    <div className="form-group">
                        <input
                            id="myForm"
                            onChange={this.onInputChange}
                            className="form-control"
                            placeholder="Insert item name"
                        />
                    </div>

                    <button
                        onClick={this.renderReminders}
                        className="btn btn-success grow"
                        type="button">
                        Add Reminder
                        </button>


                </div>
            </div>
        );
    }
}

export default Reminder;
