import React from 'react';
import './Card.css';

// destructuring
const Card = ({ id, name, height, weight, type }) => {
    return (
        <div className="tc bg-washed-blue dib br3 pa10 ma2 dim bw3 shadow-3 ba">
         <h4 className="tl ma1 b bb">&nbsp; {name}</h4>
            <img className="pa1" alt="pokemon" src={`https://pokeres.bastionbot.org/images/pokemon/${id}.png`} width="200" height="200" />
            <div className="pa3">
                <div className="bw2 ba pa1 bg-washed-green b--black-70">
                    <p className="ma0 pa1 bt">No. <strong>{id}</strong></p>
                    <p className="b">
                        Height: {height} <br />
                        Weight: {weight} <br />
                        Type: {type}
                    </p>
                </div>
            </div>
        </div>

    );
}

export default Card;
