import React, { Component } from 'react';
import './Reminder.css';
import { relativeTimeThreshold } from 'moment';

//instead of passing props.onInputChange, destructure it instead
class Reminder extends Component {
    constructor() {
        super();
        this.state = {
            input: '',
            reminders: []
        }
    }

    onInputChange = (event) => {
        this.setState({ input: event.target.value });
    }

    renderReminders = () => {

        if (this.state.input) {
            this.state.reminders.push(this.state.input)
            let ul = document.getElementById('ul');

            let li = document.createElement('li');         
            li.classList.add('list-group-item')

            let btn = document.createElement('button');
            btn.classList.add('grow btn-success');
            ul.appendChild(li);
            li.appendChild(btn);
            li.innerHTML += this.state.input;
        }
        //resetting the input bar
        this.setState({ input: '' })
        document.getElementById("myForm").value = '';
    }

    render() {
        return (
            <div className="App">
                <div fontSize="10px" className="title">
                    Reminder Pro
                </div>
                <div className="form-inline">

                    <div className="container">
                        <div className="reminder-inputs">

                            <input
                                id="myForm"
                                onChange={this.onInputChange}
                                className="form-control"
                                placeholder="Insert item name"
                            />


                            <button
                                onClick={this.renderReminders}
                                className="btn btn-success grow"
                                type="button">
                                Add Reminder
                        </button>
                        </div>
                        <div>
                            <ul id="ul" className="list-group col-l-4">

                            </ul>
                        </div>
                    </div>

                </div>
            </div>
        );
    }
}

export default Reminder;
