import React from 'react';
import './Card.css';

// destructuring
const Card = ({ count, id, name, height, weight, type }) => {
    return (
        <div className="wrapper" style={{flexBasis: '350px'}}>
        <div id={id} onClick={() => removeClass(id, name, count)} className="br3 pa10 ma2 bw2 shadow-3 parent">
            <div className="tc bg-washed-blue br3 pa10 ma2 bw3 shadow-3 ba child">
                <h4 className="tl ma1 b bb">&nbsp; {name}</h4>
                <img className="pa1" alt="pokemon" src={`https://pokeres.bastionbot.org/images/pokemon/${id}.png`} width="200" height="200" />
                <div className="pa3">
                    <div className="bw2 ba pa1 bg-washed-green">
                        <p className="ma0 pa1 bt">No. <strong>{id}</strong></p>
                        <p className="b pa1">
                        Height: {height} <br />
                        Weight: {weight} <br />
                        Type: {type}
                        </p>
                    </div>
                </div>
            </div>
        </div>
        </div>

    );
}

const removeClass = (id, name, count) => {
    let element = document.getElementById(id);
    //preventing the null from calling functions
    if (element) {
        console.log(count);
        if (count === 2) {
        element.classList.remove('parent');
        alert(`Congrats! You've unlocked a ${name}!`);
        }
    }
}

export default Card;
