import React, { Component } from 'react';
import './Milestone.css';
import Card from './Card';
import CardList from './CardList';
import { pokemon } from './pokemon';
import Push from 'push.js';
import { bake_cookie, read_cookie } from 'sfcookies';

//instead of passing props.onInputChange, destructure it instead
class Milestone extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pokemon: [],
        }
    }

    componentDidMount() {
        this.setState({ pokemon: pokemon })
    }

    render() {
        const { onRouteChange, } = this.props;
        console.log(this.props.count);
        return (
            <div>
            <h2 className="tc mt5 white shadow-4 pt3 pb3">Mystery Rewards</h2>

                <button
                    onClick={() => onRouteChange('signin')}
                    className="btn btn-secondary signout-button black grow shadow-4 mr6"
                    type="button">
                    Sign Out
                    </button>

                <button
                    onClick={() => onRouteChange('home')}
                    className="btn btn-secondary home-button black grow shadow-4 ml6"
                    type="button">
                    Home
                 </button>

                 <p className="tc white">Click on the cards to unlock rewards</p>

                <div>
                    <CardList count={this.props.count} pokemon={pokemon} />
                </div>

            </div>
        );
    }
}

export default Milestone;
