import React, { Component } from 'react';
import './Reminder.css';
import moment from 'moment';
import { bake_cookie, read_cookie } from 'sfcookies';

//instead of passing props.onInputChange, destructure it instead
class Reminder extends Component {
    constructor() {
        super();
        this.state = {
            input: '',
            dueDate: '',
            reminders: read_cookie('reminders'),
        }
    }

    onInputChange = (event) => {
        this.setState({ input: event.target.value });
    }

    onDateChange = (event) => {
        this.setState({ dueDate: event.target.value });
    }
    // renderReminders = () => {
    //     if (this.state.input) {
    //         this.state.reminders.push(this.state.input)
    //         let ul = document.getElementById('ul');

    //         let li = document.createElement('li');         
    //         li.classList.add('list-group-item')

    //         let btn = document.createElement('div');
    //         btn.classList.add('delete-button');

    //         ul.appendChild(li);
    //         li.innerHTML += this.state.input;
    //         li.appendChild(btn);
    //         btn.innerHTML += '&#x2715';
    //         bake_cookie('reminders', ul.children);
    //         console.log(ul.children)
    //     }
    //     //resetting the input bar
    //     this.setState({ input: '' })
    //     document.getElementById("myForm").value = '';
    // }

    //  renderCookies = () => {
    //     console.log('here')
    //     return read_cookie('reminders');
    //  }

    addReminders = () => {
        if (this.state.input && this.state.dueDate) {
            const reminder = {
                id: Math.random(),
                text: this.state.input,
                dueDate: this.state.dueDate
            };
            this.state.reminders.push(reminder);
            //resetting the input bar
            this.setState({ input: '' })
            document.getElementById("myForm").value = '';
            bake_cookie('reminders', this.state.reminders);
            console.log('added reminders', this.state.reminders);
        }
    }

    clearReminders = () => {
        this.setState({ reminders: [] });
        bake_cookie('reminders', this.state.reminders);
        console.log('clear',  this.state.reminders);
    }

    deleteReminders = (id) => {
        const updatedReminders = this.state.reminders.filter(reminder => reminder.id !== id);
        this.setState({ reminders: updatedReminders });
        bake_cookie('reminders', updatedReminders);
        console.log('deleted cookies', this.state.reminders);
    }

    displayReminders = () => {
        const reminderList = this.state.reminders.map(reminder => {
            return (
                <ul id="ul" className="list-group col-l-4">
                    <li key={reminder.id} className="list-group-item shadow-4">
                    <button onClick={() => this.deleteReminders(reminder.id)} className="btn btn-primay grow">&#x2713;</button>
                        <div className="list-item">
                            <div>{reminder.text}</div>
                            <div><em>{moment(new Date(reminder.dueDate)).calendar()}</em></div>
                            <div><em>{moment(new Date(reminder.dueDate)).fromNow()}</em></div>
                        </div>
                        <button onClick={() => this.deleteReminders(reminder.id)} className="btn btn-danger grow">&#x2715;</button>
                    </li>
                </ul>
            );
        })
        return reminderList;
    }

    render() {
        return (
            <div className="App">
                <div className="title br3">
                  <h1><strong>B U R P E E </strong></h1>
                  <p><em>Log your reminders here</em></p>
                </div>
                <div className="form-inline">
                    <div className="container">          
                        <div className="reminder-inputs br3 shadow-5">    
                            <input
                                id="myForm"
                                onChange={this.onInputChange}
                                className="form-control"
                                placeholder="Insert item name"
                            />
                                 
                            <input
                                id="myForm"
                                onChange={this.onDateChange}
                                className="form-control"
                                type="datetime-local"
                            />
                         
                            <button
                                onClick={this.addReminders}
                                className="btn btn-success grow add-button br2 shadow-4"
                                type="button">
                                Add Reminder
                            </button>
                        </div>
                            <div>
                                {this.displayReminders()}
                            </div>

                            <button
                                onClick={this.clearReminders}
                                className="btn btn-success clear-button grow shadow-4"
                                type="button">
                                Clear All Reminders
                            </button>
                        </div>

                </div>
            </div>
        );
    }
}

export default Reminder;
