import React from 'react';

// destructuring
const Card = ({ id, name, height, weight, type }) => {
    return (
        <div className="tc bg-washed-green dib br3 pa10 ma2 grow bw2 shadow-3 ba">
            <img className="pa1" alt="pokemon" src={`https://pokeres.bastionbot.org/images/pokemon/${id}.png`} width="200" height="200" />
            <div>
                <p>No. {id}</p>
                <h2>{name}</h2>
                <p>
                Height: {height} <br/>
                Weight: {weight} 
                Type: {type}
                </p>
            </div>
        </div>

    );
}

export default Card;
