import React, { Component } from 'react';
import './Reminder.css';

//instead of passing props.onInputChange, destructure it instead
class Reminder extends Component {
    constructor() {
        super();
        this.state = {
            input: '',
            reminders: []
        }
    }

    onInputChange = (event) => {
        this.setState({ input: event.target.value });
    }

    renderReminders = () => {
        this.setState({ input: '' })
        document.getElementById("myForm").value = '';
        console.log(this.state.reminders, this.state.input);

        return (
            <ul className="list-group col-sm-4">
                {
                    this.state.reminders.map(reminder => {
                        
                            <li className="list-group-item">
                                <div>this.state.input</div>
                            </li>
                        
                    })
                }   
            </ul>
        )
            
    }

    render() {
        return (
            <div className="App">
                <div fontSize="10px" className="title">
                    Reminder Pro
                </div>
                <div className="form-inline">
                   
                        <div className="form-group">
                            <input
                                id="myForm"
                                onChange={this.onInputChange}
                                className="form-control"
                                placeholder="Insert item name"
                            />
                        </div>

                        <button
                            onClick={this.renderReminders}
                            className="btn btn-success grow"
                            type="button">
                            Add Reminder
                        </button>   
             

                </div>
            </div>
        );
    }
}

export default Reminder;
