import React, { Component } from 'react';
import './Milestone.css';
import CardList from './CardList';
import { pokemon } from './pokemon';

class Milestone extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pokemon: [],
        }
    }

    componentDidMount() {
        this.setState({ pokemon: pokemon })
        console.log('count inside milstone', this.props.count);
    }

    render() {
        const { onRouteChange, } = this.props;
        return (
            <div>
            <h2 className="tc mt5 white shadow-4 pt3 pb3">Mystery Rewards</h2>
         

                 <p className="mt4 tc white">Click on the cards to unlock rewards</p>  
                 <p className="tc white">Expiry prevented {this.props.count}..</p>
                 <nav></nav>
                <div>
                    <CardList count={this.props.count} pokemon={pokemon} />
                </div>

            </div>
        );
    }
}

export default Milestone;
